import React from 'react'
import { useState} from 'react'
import {Modal, ModalBody, Button} from 'react-bootstrap'

const Alert = (prop)=>{

    let [show,setShow] = useState(false)

    const Atualiza = () =>{
        useState(prop.exibeModal)
    }

    let messageY = 'Login realizado com sucesso!'
    let messageN = 'Login NÃO realizado com sucesso!'
    let message = (prop.validation) && (prop.validation==='Y')?messageY:messageN 


    return(
        <>
            <Modal show={show} onHide={() => setShow(false)}>
                <ModalBody>
                    <h1>
                        {message}
                    </h1>
                </ModalBody>
            </Modal>
            <Button onClick={()=>setShow(true)}>Abrir Modal</Button>
        </>
    )
}

export default Alert
