import React from "react";
import { useState } from "react";
import {TextField, FormControl, Box, Button, Container} from '@mui/material'

const Login = ()=>{

    var [value,setValue] = useState({"valor":""})
    const inputForm = new FormData(document.querySelector('#formulario'))
    var valorTratado = ""

    const SalvaLocalStorage = ()=>{
        let data = {"nome":document.querySelector("#nome").value}
        localStorage.setItem("data",data)
        console.log(data.nome)   


        console.log(inputForm.get("id"));

    }

    const DuplicaValor = ()=>{
        let valor = document.querySelector("#nome").value 
        let valorSplit = valor.split("")

        for(let i = 0; i<valor.length; i++){
            valorSplit[i] = "|"+valor[i]
            valorTratado += valorSplit[i]

        }    
        setValue({"valor":valorTratado})
    }



    return(
        <>
            <Container fixed Style={"text-align:center"}>
                <Box   sx={{'& .MuiTextField-root': { m: 0.5, width: '25ch' }}}
                        component="form"
                        autocomplete="off">
                    <form id="formulario">
                        <input type="text" size="small" name="nome" id="nome" label="Nome Completo" variant="outlined"  onChange={() => DuplicaValor()}/>
                        <TextField size="small" name = "email" id="email" label="email" type="email" />
                        <TextField size="small" name = "repeteco" id="repeteco" label="Repeteco" type="text" value={value.valor}/>
                        <Button variant="contained" color="success" onClick={() => SalvaLocalStorage()}>Enviar</Button>
                    </form>
                </Box>
            </Container>
        </>
    )
}

export default Login
