import React from "react";
import { useState } from "react";
import {TextField, FormControl, Box, Button, Container} from '@mui/material'

const Login = ()=>{

    var [value,setValue] = useState({"valor":""})

    var valorTratado = ""

    const SalvaLocalStorage = ()=>{
        let data = {"nome":document.querySelector("#nome").value}
        localStorage.setItem("data",data)
        console.log(data.nome)   
    }

    const DuplicaValor = ()=>{
        let valor = document.querySelector("#nome").value 
        let valorSplit = valor.split("")

        for(let i = 0; i<valor.length; i++){
            valorSplit[i] = "|"+valor[i]
            valorTratado += valorSplit[i]

        }    
        setValue({"valor":valorTratado})
    }

    return(
        <>
        <Container maxWidth="xl">
        <Box   sx={{'& .MuiTextField-root': { m: 0.5, width: '25ch' }}}
                component="form"
                autocomplete="off">
            <FormControl variant="outlined">
                <TextField size="small" id="nome" label="Nome Completo" variant="outlined"  onChange={() => DuplicaValor()}/>
                <TextField id="nome_" type="label" value={value.valor?value.valor:''}/>
                <Button variant="contained" color="success" onClick={() => SalvaLocalStorage()}>Enviar</Button>

            </FormControl>
        </Box>
        </Container>
        </>
    )
}

export default Login
