import React from "react";
import { useState } from "react";

const Login = ()=>{

    var [value,setValue] = useState({"valor":""})

    var valorTratado = ""

    const SalvaLocalStorage = ()=>{
        let data = {"nome":document.querySelector("#nome").value}
        localStorage.setItem("data",data)
        console.log(data.nome)   
    }

    const DuplicaValor = ()=>{
        let valor = document.querySelector("#nome").value 
        let valorSplit = valor.split("")

        for(let i = 0; i<valor.length; i++){
            valorSplit[i] = "|"+valor[i]
            valorTratado += valorSplit[i]

        }    
        setValue({"valor":valorTratado})
    }

    return(
        <>
            <label>Digite algo:</label>
            <input id="nome" type="text" onChange={() => DuplicaValor()}/>
            <input id="nome_" type="label" value={value.valor?value.valor:''}/>
            <button value="Enviar" onClick={() => SalvaLocalStorage()}>Enviar</button>

        </>
    )
}

export default Login
