import {React, useState} from "react";
import {Modal, ModalBody, Container,Form,Button} from 'react-bootstrap'
import Logo from '../img/logo.svg'

const TelaLogin = () =>{

    //define dados de acesso
    let dados = {user:"jp",password:"123"}

    //seta valores no localStorage
    localStorage.setItem("users",JSON.stringify(dados))

    console.log(JSON.parse(localStorage.getItem("users")).pswd)

    //let messageY = 'Login realizado com sucesso!'
    //let messageN = 'Login NÃO realizado =('
    const [showModal,setShowModal] = useState(false)
    const [messageModal,setMessageModal] = useState("Credeciais inválidas!")
    const VerificaCredenciais = ()=>{
        let user = JSON.parse(localStorage.getItem("users")).user === document.querySelector("#user").value
        let password = JSON.parse(localStorage.getItem("users")).password === document.querySelector("#password").value
        let validacao = user && password ? true: false

        if(validacao===true){
            setMessageModal("Credenciais válidas")
        }

        setShowModal(true)
    }

    return (
        <>

            <Container className="d-flex justify-content-center mt-5" >    
                <Modal show={showModal} onHide={() => setShowModal(false)}>
                    <ModalBody>
                        <h1>
                           {messageModal}
                        </h1>
                    </ModalBody>
                </Modal>
            <Form className="col-sm-8 col-md-4 ">
                    <Form.Group className="mb-3" style={{textAlign:"center"}}>
                        <img src={Logo} alt="logo" style={{width:"100px"}}/>
                        <h4 className="mt-2">You System</h4>
                    </Form.Group>    
                    <Form.Group className="mb-2">
                        <Form.Label>Usuário:</Form.Label>
                        <Form.Control type="text" name="user" id="user"/>
                    </Form.Group>
                    <Form.Group className="mb-2">
                        <Form.Label>Senha:</Form.Label>
                        <Form.Control type="password" name="pswd" id="pswd"/>
                    </Form.Group>
                    <Form.Group className="mb-2">
                        <Form.Check type="checkbox" label="Lembre-me" />
                    </Form.Group>
                    <Button variant="success" className="col-12" onClick={()=>VerificaCredenciais()}>Acessar</Button>
                </Form>
            </Container>
        </>
    )
}

export default TelaLogin
